targets.expr:
sum (
  increase(worker_execution_unknown_errors_total{cluster=~"^($cluster)$", namespace=~"^($namespace)$", pod=~"^($pod)$", company=~"^($company)$"}[$__range])
) by (namespace, company) > 0

sum(
    increase(worker_execute_scripts_total{cluster=~"^($cluster)$", namespace=~"^($namespace)$", pod=~"^($pod)$", company=~"^($company)$"}[$interval])
) by (namespace, company, source) > 0

sum(
    increase(worker_execution_system_errors_total{cluster=~"^($cluster)$", namespace=~"^($namespace)$", pod=~"^($pod)$", company=~"^($company)$"}[$__range])
) by (namespace, company) > 0

sum(
    increase(worker_execution_timeout_errors_total{cluster=~"^($cluster)$", namespace=~"^($namespace)$", pod=~"^($pod)$", company=~"^($company)$"}[$__range])
) by (namespace, company) > 0

topk(5, sum(
    increase(worker_execute_scripts_total{cluster=~"^($cluster)$", namespace=~"^($namespace)$", company=~"^($company)$"}[$interval])
) by (namespace, company, source))

histogram_quantile(0.95,
  sum by (le, namespace, company, source) (rate(worker_script_compile_time_histogram_bucket{cluster=~"^($cluster)$", namespace=~"^($namespace)$", pod=~"^($pod)$", company=~"^($company)$"}[$__rate_interval]))
)

topk(5, 
  histogram_quantile(0.95, sum(rate(worker_script_execution_time_histogram_bucket{cluster=~"^($cluster)$", namespace=~"^($namespace)$", pod=~"^($pod)$", company=~"^($company)$"}[$__rate_interval])) by (le, namespace, company, source))
) > 0
